import React from 'react'
import Square from '../Square/Square'
import './Board.css'

class Board extends React.Component {

    //by Mouse click on Square component
    handleClick(i) {
        //create copy of the squares array
        const squares = this.state.squares.slice()
        //ignoring a click if someone has won/square fild is filed
        if (calculateWinner(squares) || squares[i]) {
            return;
        }
        //assigne the xIsNext actual value to [i]- ellement of coppied array 
        squares[i] = this.state.xIsNext ? 'X' : 'O'
        //renew the state
        this.setState({
                        squares,
                        xIsNext: !this.state.xIsNext})

    }

    //rendering Square component
    renderSquare(i) {
        return <Square 
                    value={this.props.squares[i]}
                    onClick={() => this.props.onClick(i)}
                />
    }
    render() {
        return(
            <div>
                <div className='board-row'>
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>

                <div className='board-row'>
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>

                <div className='board-row'>
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>

            </div>

        )
    }
}



export default Board
